def solve(A):
    def findLPS(subA): 
        M = len(subA)
        longestPreSuf = [None] * M 
        length = 0
        longestPreSuf[0] = 0          
        i = 1
        while i < M:         
            if subA[i] == subA[length]:         
                length += 1
                longestPreSuf[i] = length
                i += 1             
            else:         
                if length != 0:             
                    length = longestPreSuf[length - 1]             
                else:              
                    longestPreSuf[i] = 0
                    i += 1
        return longestPreSuf    
    rev_A = A[::-1]    

    temp = A + "%" + rev_A
    lps = findLPS(temp)
    return len(A) - lps[-1]







